let num1 = 10;
let num2 = 20;

console.log(`num1: ${num1} and num2: ${num2}`);
console.log("Comparing num1 and num2...");

if(num1 > num2){
    console.log("\nnum1 is greater than num2");
}else if(num1 < num2){
    console.log("\nnum1 is less than num2");
}else{
    console.log("\nnum1 is equal to num2");
}